#include <iostream>using namespace std;int main(){	int firstTone, secondTone, octavesOne, octavesTwo;	cout << "Enter first tone: ";	cin >> firstTone;	cout << "Enter first tone octave: ";	cin >> octavesOne;	cout << "Enter second tone: ";	cin >> secondTone;	cout << "Enter second octave: ";	cin >> octavesTwo;	if ((octavesOne > 8 || octavesOne < 1) || (octavesTwo > 8 || octavesTwo < 1) ||		((firstTone > 8 || firstTone < 1) || (secondTone > 8 || secondTone < 1)))	{		cerr << "Invalid input";		return 1;	}	if (secondTone < firstTone)	{		int temp = secondTone;		secondTone = firstTone;		firstTone = secondTone;	}	int resultCombination = secondTone - firstTone;	// If the intevals are septima or secunda	if (resultCombination == 1 || resultCombination == 6)	{		cout << "fake" << endl;	}	else if (resultCombination == 2)	{		cout << "soundy" << endl;	}	else if (resultCombination == 4)	{		cout << "hollow" << endl;	}	else	{		cout << "normal" << endl;	}	return 0;}